properties.widgets.SapDelta-cdcSource:
{
  "outputs": [],
  "metadata": {"spec-version": "1.5"},
  "configuration-groups": [
    {
      "label": "Basic",
      "properties": [
        {
          "name": "host",
          "widget-type": "textbox",
          "label": "Host"
        },
        {
          "name": "port",
          "widget-type": "textbox",
          "label": "Port",
          "widget-attributes": {"default": "1433"}
        },
        {
          "name": "database",
          "widget-type": "textbox",
          "label": "Database Name"
        }
      ]
    },
    {
      "label": "Credentials",
      "properties": [
        {
          "name": "user",
          "widget-type": "textbox",
          "label": "User"
        },
        {
          "name": "password",
          "widget-type": "password",
          "label": "Password"
        }
      ]
    },
    {
      "label": "Advanced",
      "properties": [
        {
          "name": "serverTimezone",
          "widget-type": "textbox",
          "label": "Server Timezone",
          "widget-attributes": {"default": "UTC"}
        },
        {
          "name": "replicateExistingData",
          "widget-type": "toggle",
          "label": "Replicate Existing Data",
          "widget-attributes": {
            "default": "true",
            "off": {
              "label": "No",
              "value": "false"
            },
            "on": {
              "label": "Yes",
              "value": "true"
            }
          }
        }
      ]
    }
  ],
  "display-name": "SAP Delta Replicator"
}
